import 'package:chatgpt/presentation/pages/chat/chat_page.dart';import 'package:flutter/material.dart';class SplashPage extends StatefulWidget {  const SplashPage({super.key});  @override  State<SplashPage> createState() => _SplashPageState();}class _SplashPageState extends State<SplashPage> {  @override  void initState() {    super.initState();    Future.delayed(        const Duration(          seconds: 3,        ), () {      Navigator.pushReplacement(          context, MaterialPageRoute(builder: (context) => const ChatPage()));    });  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: Stack(        children: [          Container(            decoration: const BoxDecoration(              color: Color(0xFF1b1b1b)            ),            child: Padding(              padding: const EdgeInsets.only(top: 56),              child: Image.asset(              'assets/images/dot.jpg',              width: MediaQuery.of(context).size.width,              height: MediaQuery.of(context).size.height,            ),            ),          )        ],      ),    );  }}